import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './hero.css';
import hand_icon from '../../Assets/hand_icon.png';
import arrow_icon from '../../Assets/arrow.png';
import hero_icon from '../../Assets/hero_icon.webp';

const slides = [
  {
    image: hero_icon,
    headline: 'NEW ARRIVALS ONLY',
    subtext: 'collections for everyone',
    cta: 'Latest Collections',
    bg: 'rgba(102,126,234,0.7)',
  },
  {
    image: 'https://images.unsplash.com/photo-1512436991641-6745cdb1723f?auto=format&fit=crop&w=800&q=80',
    headline: 'SUMMER SALE',
    subtext: 'Up to 50% off on selected styles',
    cta: 'Shop Summer',
    bg: 'rgba(255,107,107,0.7)',
  },
  {
    image: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=800&q=80',
    headline: 'TRENDING NOW',
    subtext: 'Discover the latest trends',
    cta: 'Explore Trends',
    bg: 'rgba(118,75,162,0.7)',
  },
];

export const Hero = () => {
  const [current, setCurrent] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => {
      setCurrent((prev) => (prev + 1) % slides.length);
    }, 4000);
    return () => clearTimeout(timer);
  }, [current]);

  const goToCollections = () => {
    navigate('/latest-collections');
  };

  return (
    <div className="hero-slider-container">
      {slides.map((slide, idx) => (
        <div
          key={idx}
          className={`hero-slide${idx === current ? ' active' : ''}`}
          style={{ backgroundImage: `url(${slide.image})`, backgroundColor: slide.bg }}
        >
          <div className="hero-slide-overlay">
            <div className="hero-slide-content">
              <h2>{slide.headline}</h2>
              <div className="hand-icon">
                <p>{idx === 0 ? 'new' : ''}</p>
                {idx === 0 && <img src={hand_icon} alt="hand icon" />}
              </div>
              <p className="hero-text">{slide.subtext}</p>
              <button className="hero-latest-btn" onClick={goToCollections}>
                <span>{slide.cta}</span>
                <img src={arrow_icon} alt="arrow icon" />
              </button>
            </div>
          </div>
        </div>
      ))}
      <div className="hero-slider-dots">
        {slides.map((_, idx) => (
          <span
            key={idx}
            className={`dot${idx === current ? ' active' : ''}`}
            onClick={() => setCurrent(idx)}
          />
        ))}
      </div>
    </div>
  );
};
