import React, { useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './hero.css';
import hand_icon from '../../Assets/hand_icon.png';
import arrow_icon from '../../Assets/arrow.png';
import hero_icon from '../../Assets/hero_icon.webp';

export const Hero = () => {
  const navigate = useNavigate();
  const imageRef = useRef(null);
  const heroRef = useRef(null);

  // Parallax mouse move effect
  const handleMouseMove = (e) => {
    const hero = heroRef.current;
    const img = imageRef.current;
    if (!hero || !img) return;
    const rect = hero.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const xPercent = (x / rect.width - 0.5) * 2; // -1 to 1
    const yPercent = (y / rect.height - 0.5) * 2; // -1 to 1
    img.style.transform = `scale(1.05) translate(${xPercent * 18}px, ${yPercent * 12}px)`;
  };
  const handleMouseLeave = () => {
    const img = imageRef.current;
    if (img) img.style.transform = '';
  };

  // Button ripple effect
  const handleButtonClick = (e) => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const diameter = Math.max(btn.clientWidth, btn.clientHeight);
    const radius = diameter / 2;
    circle.style.width = circle.style.height = `${diameter}px`;
    circle.style.left = `${e.clientX - btn.getBoundingClientRect().left - radius}px`;
    circle.style.top = `${e.clientY - btn.getBoundingClientRect().top - radius}px`;
    circle.className = 'ripple';
    btn.appendChild(circle);
    setTimeout(() => circle.remove(), 600);
  };

  const goToCollections = (e) => {
    handleButtonClick(e);
    setTimeout(() => navigate('/latest-collections'), 200); // allow ripple to show
  };

  return (
    <div className="hero-container animated-gradient-bg">
      <div className="hero" ref={heroRef} onMouseMove={handleMouseMove} onMouseLeave={handleMouseLeave}>
        <div className="hero-left">
          <h2>NEW ARRIVALS ONLY</h2>
          <div className="hero-text-container">
            <div className="hand-icon">
              <p>new</p>
              <img src={hand_icon} alt="hand icon" />
            </div>
            <p className="hero-text">collections</p>
            <p className="hero-text">for everyone</p>
          </div>
          <button 
            className="hero-latest-btn"
            onClick={goToCollections}
          >
            <span>Latest Collections</span>
            <img src={arrow_icon} alt="arrow icon" />
          </button>
        </div>
        <div 
          className="hero-right" 
          style={{ cursor: 'pointer' }}
        >
          <img ref={imageRef} src={hero_icon} alt="hero banner" className="hero-image parallax-img" />
        </div>
      </div>
    </div>
  );
};
